// tailwind.config.js

import React, { useState, useEffect } from "react";
const images = [
  "pic1.jpg",
  "pic2.jpg",
  "pic3.jpg",
  "pic4.jpg",
];

export default function Fst() {
  const [currentIndex, setCurrentIndex] = useState(0);
useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
<div class="md:flex">
    <div class="md:shrink-0">
      <img
        class="h-48 w-full object-cover md:h-full md:w-48"
        src="/img/building.jpg"
        alt="Modern building architecture"
      />
    </div>
    <div class="p-8">
      <div class="text-sm font-semibold tracking-wide text-indigo-500 uppercase">Company retreats</div>
      <a href="#" class="mt-1 block text-lg leading-tight font-medium text-black hover:underline">
        Incredible accommodation for your team
      </a>
      <p class="mt-2 text-gray-500">
        Looking to take your team away on a retreat to enjoy awesome food and take in some sunshine? We have a list of
        places to do just that.
      </p>
    </div>
  </div>


      <div className="flex items-center flex-none pl-5 sm:pl-6 pr-4 lg:pr-6 position:relative z-10 top-0 left-0 -mb-px antialiased">
        <div className="relative items-center ml-20">
          <div className="relative mx-auto">
            <span className="text-4xl font-semibold text-dark">BESTCHILL INC.,</span>
          </div>
          <div className="relative mx-auto">
            <span className="text-3xl font-semibold mx-auto text-dark">Your Best Choice to Coolness!</span>
          </div>
        </div>

        <div className="clip-custom-shape -mt-20 mx-auto h-[450px] w-[800px] right-0 absolute">
          {/* <div className="relative overflow-hidden w-full h-[500px]"> */}
          <div className="flex animate-loopRightDouble">
            <img
              src={images[currentIndex]}
              alt={`Slide ${currentIndex + 1}`}
              className="w-full h-full object-[25%_75%] size-1"
            />
            {/* Repeat if needed for smooth loop */}
            <img
              src={images[currentIndex]}
              alt={`Slide ${currentIndex + 4}`}
              className="w-full h-full object-[25%_75%] size-1"
            />
          </div>
        </div>
      </div>

      

    </>
  );
}
