// tailwind.config.js

import React, { useState, useEffect } from "react";
const images = [
  "pic1.jpg",
  "pic2.jpg",
  "pic3.jpg",
  "pic4.jpg",
];

export default function Fst() {
  module.exports = {
  theme: {
    extend: {
      keyframes: {
        loopRightDouble: {
          '0%': { transform: 'translateX(0)' },
          '100%': { transform: 'translateX(-50%)' }, // adjust for content width
        },
      },
      animation: {
        loopRightDouble: 'loopRightDouble 70s linear infinite',
      },
    },
  },
};
  return (
    <div className="relative mx-auto h-[500px] w-full bg-[#0C59C3]">
    {/* <div className="relative overflow-hidden w-full h-[500px]"> */}
      <div className="flex animate-loopRightDouble w-max">
        <img
        src={images[currentIndex]}
        alt={`Slide ${currentIndex + 1}`}
        className="w-full h-full object-cover"
      />
        {/* Repeat if needed for smooth loop */}
        <img
        src={images[currentIndex]}
        alt={`Slide ${currentIndex + 1}`}
        className="w-full h-full object-cover"
      />
      </div>
    </div>
  );
}
