import React, { useState, useEffect } from "react";

import Prodreefer from "../components/Prodreefer.jsx";

const images = [
  "/attachment/pic1.jpg",
  "/attachment/pic2.jpg",
  "/attachment/pic3.jpg",
  "/attachment/pic4.jpg",
];

export default function Products() {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto loop every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  // Manual navigation
  const goPrev = () => {
    setCurrentIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const goNext = () => {
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  return (
    <>
      <div className="flex h-[400px] w-full pt-[10px]">
        {/* <img
          src={images[currentIndex]}
          alt={`Slide ${currentIndex + 1}`}
          className="w-full h-full object-cover"
        /> */}
        {/* <div className="w-full h-full object-cover">
        <Prodreefer/>
        </div> */}

        

        <div className="relative flex flex-col items-center justify-center w-full overlay-hidden">
              <img
                  src="/attachment/Reefer.png"
                  // alt={`Slide ${currentIndex + 1}`}
                  alt = ""

                  className="absolute inset-0 w-1/2 h-full"
                />
            </div>
        {/* <button
          onClick={goPrev}
          className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-white bg-opacity-0 p-2 rounded-full shadow"
        >
          ◀
        </button>
        <button
          onClick={goNext}
          className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-white bg-opacity-0 p-2 rounded-full shadow"
        >
          ▶
        </button> */}
        <div className="absolute -bottom-1 left-1/2 transform -translate-x-1/2 flex space-x-2 p-2">
          {images.map((_, idx) => (
            <button
              key={idx}
              onClick={() => setCurrentIndex(idx)}
              className={`w-3 h-3 rounded-full ${idx === currentIndex ? "bg-blue-600" : "bg-gray-300"
                }`}
            />
          ))}
        </div>
      </div>
      <div>
        <button>
          See more Product!
        </button>
      </div>



      <div className="flex flex-col md:flex-row items-center bg-white rounded-md w-full overflow-hidden">
        <div className="flex flex-col md:flex-row w-full ">
          <div className="p-8 md:w-1/2 w-full flex flex-col items-center justify-center">
            <h1 className="text-3xl font-bold text-indigo-600 uppercase tracking-wide">
              Your Best Choice to Coolness!
            </h1>

          </div>

          <div className="md:w-100% w-full">
            <div className="clip-custom-shape flex flex-col items-center justify-center -ml-[70px] right-1">
              <img
                  src="/attachment/Reefer.png"
                  // alt={`Slide ${currentIndex + 1}`}
                  alt = ""

                  className="object-cover md:w-full h-full"
                />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
