import { useState } from 'react';
import SuccessModal from '../components/SuccessModal';

function BookAService() {
    const [showSuccess, setShowSuccess] = useState(false);
    const [isChecked, setIsChecked] = useState(false);

    const handleCheckboxChange = (e) => {
        setIsChecked(e.target.checked);
    };

    const [formData, setFormData] = useState({
        FName: '',
        LName: '',
        Company: '',
        Email: '',
        PhoneNumber: '',
        Message: ''
    });

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:5000/InquiriesPost/submitInquiries', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });

            if (response.ok) {
                const result = await response.json();
                // alert('Inquiry submitted successfully!');
                console.log(result);
                console.log(formData);
                setFormData({
                    FName: '',
                    LName: '',
                    Company: '',
                    Email: '',
                    PhoneNumber: '',
                    Message: ''
                });
                setShowSuccess(true);
            } else {
                alert('Failed to submit inquiry');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Error submitting inquiry');
        }
    };

    const services = ['PREVENTIVE MAINTENANCE SERVICES', 'REFRIGERATION AND AC UNIT REPAIR', 'BODY AND COLLISION REPAIR']
    const product = [
        'REEFER SYSTEM TS 26 (12V)',
        'REEFER SYSTEM TS 26 (24V)',
        'REEFER SYSTEM TS 55 (12V)',
        'REEFER SYSTEM TS 55 (24V)',
        'PORTABLE AIRCON DL-1800 (12V)',
        'PORTABLE AIRCON DL-1800 (24V)',
        'PORTABLE AIRCON DL-2600F (12V)',
        'PORTABLE AIRCON DL-2600F (24V)',
        'PORTABLE AIRCON DL-2600FT (12V)',
        'PORTABLE AIRCON DL-2600FT (24V)']

    return (
        <>
            <div className="max-w-3xl mx-auto flex h-auto">
                <form action="#" method="POST" className="h-full mx-auto mt-10 w-full" onSubmit={handleSubmit}>
                    <div className="grid grid-cols-1 gap-x-4 gap-y-2 sm:grid-cols-2 flex w-full">
                        <div>
                            <label className="block text-sm/6 font-semibold text-gray-900">*Name</label>
                            <input id="FullName" type="text" name="FullName" placeholder='Full Name' value={formData.FName} onChange={handleChange}
                                className="block w-full rounded-md bg-white px-3.5 py-2 text-base text-gray-900 outline-1 -outline-offset-1 outline-gray-300 placeholder:text-gray-400 focus:outline-2 focus:-outline-offset-2 focus:outline-indigo-600" />
                        </div>

                        <div className="">
                            <label className="block text-sm/6 font-semibold text-gray-900">*Email</label>
                            <input id="email" type="email" name="Email" placeholder='Email Address' value={formData.Email} onChange={handleChange}
                                className="block w-full rounded-md bg-white px-3.5 py-2 text-base text-gray-900 outline-1 -outline-offset-1 outline-gray-300 placeholder:text-gray-400 focus:outline-2 focus:-outline-offset-2 focus:outline-indigo-600" />
                        </div>
                        <div className=''>
                            <label className="block text-sm/6 font-semibold text-gray-900">*Company Name</label>
                            <input id="lastname" type="text" name="LName" placeholder='Company Name' value={formData.LName} onChange={handleChange}
                                className="block w-full rounded-md bg-white px-3.5 py-2 text-base text-gray-900 outline-1 -outline-offset-1 outline-gray-300 placeholder:text-gray-400 focus:outline-2 focus:-outline-offset-2 focus:outline-indigo-600" />
                        </div>
                        <div className="">
                            <label className="block text-sm/6 font-semibold text-gray-900">*Phone</label>
                            <input id="company" type="text" name="Company" placeholder='Contact Number' value={formData.Company} onChange={handleChange}
                                    className="block w-full rounded-md bg-white px-3.5 py-2 text-base text-gray-900 outline-1 -outline-offset-1 outline-gray-300 placeholder:text-gray-400 focus:outline-2 focus:-outline-offset-2 focus:outline-indigo-600" />
                        </div>
                        <div className="">
                            <label className="block text-sm/6 font-semibold text-gray-900">*Service</label>
                            <div className="h-[38px]">
                                <select className="w-full h-full flex rounded-md bg-white outline-1 -outline-offset-1 outline-gray-300 has-[input:focus-within]:outline-2 has-[input:focus-within]:-outline-offset-2 has-[input:focus-within]:outline-indigo-600">
                                    <option value="">{'--- Please choose an option ---'}</option>
                                    {services.map((services) => (
                                        <option value={services}>{services}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className="">
                            <label className="block text-sm/6 font-semibold text-gray-900">*Product</label>
                            <div className="h-[38px]">
                                <select className="w-full h-full flex rounded-md bg-white outline-1 -outline-offset-1 outline-gray-300 has-[input:focus-within]:outline-2 has-[input:focus-within]:-outline-offset-2 has-[input:focus-within]:outline-indigo-600">
                                    <option value="">{'--- Please choose an option ---'}</option>
                                    {product.map((product) => (
                                        <option value={product}>{product}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className="h-full">
                            <label className="block text-sm/6 font-semibold text-gray-900">*Schedule</label>
                            <div className=" flex flex-row w-full gap-2">
                                <div className='w-full'>
                                    <label className="block text-sm/6 font-semibold text-gray-500">Date</label>
                                    <input
                                        className="items-center justify-center w-full h-[38px] flex rounded-md bg-white outline-1 -outline-offset-1 outline-gray-300 has-[input:focus-within]:outline-2 has-[input:focus-within]:-outline-offset-2 has-[input:focus-within]:outline-indigo-600"
                                        type="date"
                                        name="your-date" />
                                </div>
                                <div className='w-full h-full'>
                                    <label className="block text-sm/6 font-semibold text-gray-500">Time</label>
                                    <input
                                        className="items-center justify-center w-full h-[38px]  flex rounded-md bg-white outline-1 -outline-offset-1 outline-gray-300 has-[input:focus-within]:outline-2 has-[input:focus-within]:-outline-offset-2 has-[input:focus-within]:outline-indigo-600"
                                        type="time"
                                        name="your-date" />

                                </div>
                            </div>
                        </div>
                        <div className="h-full">
                            <label className="block text-sm/6 font-semibold text-gray-900">*Remarks</label>
                            <div className="">
                                <textarea id="message" name="Message" rows="4" placeholder='Remarks' value={formData.Message} onChange={handleChange}
                                    className="block w-full rounded-md bg-white px-3.5 py-2 text-base text-gray-900 outline-1 -outline-offset-1 outline-gray-300 placeholder:text-gray-400 focus:outline-2 focus:-outline-offset-2 focus:outline-indigo-600"></textarea>
                            </div>
                        </div>
                    </div>

                    <div className='mt-2.5' >
                        <label className='text-justify italic text-gray-600'>
                            * By submitting your contact information, you are allowing us to contact you through SMS, Email or Phone calls about our products and services. You also consent and agree to our privacy policy when you click on the 'Submit' button. Coolaire Consolidated, Inc. does not intentionally market to minors, hence, by submitting this request, you are confirming that you are of legal age. Coolaire understands the importance of your online privacy and your data will only be shared within the Coolaire Consolidated Inc and third-party vendors are excluded.
                        </label>
                    </div>
                    <div className='flex w-full justify-center gap-2'>
                        <input type="checkbox" checked={isChecked} onChange={handleCheckboxChange} />
                        <label>{' I accept the Terms and Conditions'}</label>
                    </div>
                    <div className="my-5 flex w-1/3 mx-auto h-full items-center justify-center">
                        <button type="submit" className={`${isChecked ? 'bg-blue-600 hover:bg-blue-700' : 'bg-gray-400 cursor-not-allowed'} block w-full rounded-md bg-indigo-600 px-3.5 py-2.5 text-center text-sm font-semibold text-white shadow-xs hover:bg-indigo-500 focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600`}>SUBMIT</button>
                    </div>
                </form>
            </div>
        </>
    )
}


export default BookAService;
