import React, { useState, useEffect } from "react";

import Prodreefer from "../components/Prodreefer.jsx";
import ProductsReefer from "../components/ProductsReefer.jsx";
import ProductsPortable from "../components/ProductsPortable.jsx";

const pages = [
  <ProductsReefer />,
  <ProductsPortable />,  
];

export default function Products() {
  const [currentPage, setCurrentPage] = useState(0);

  const nextPage = () => {
    setCurrentPage((prev) => (prev + 1) % pages.length);
  };

  const prevPage = () => {
    setCurrentPage((prev) => (prev - 1 + pages.length) % pages.length);
  };

  return (
    <div className="bg-gradient-to-r from-blue-800 via-blue-500 to-blue-300">

      <div className="flex mx-auto w-full items-center justify-center p-4">
        <h1 className="text-4xl font-bold text-indigo-600 uppercase tracking-wide">
          Product
        </h1>
      </div>

      <div className="flex flex-col md:flex-row items-center bg-white rounded-md w-full overflow-hidden m ">
        {pages[currentPage]}
      </div>
        
        <div className="absolute -bottom-1 left-1/2 transform -translate-x-1/2 flex space-x-2 p-2">
          {pages.map((Page, index) => (
            <button
              key={index}
              onClick={() => setCurrentPage(index)}
              className={`w-3 h-3 rounded-full ${index === setCurrentPage ? "bg-blue-600" : "bg-gray-300"
                }`}
            />
          ))}
        </div>

       <div className="absolute bottom-5 left-1/2 transform -translate-x-1/2 flex gap-4">
        <button
          onClick={prevPage}
          className="px-4 py-2 bg-gray-700 text-white rounded hover:bg-gray-900"
        >
          Prev
        </button>
        <button
          onClick={nextPage}
          className="px-4 py-2 bg-gray-700 text-white rounded hover:bg-gray-900"
        >
          Next
        </button>
      </div>

      <div>
        <button>
          See more Product!
        </button>
      </div>
    </div>
  );
}
