import React, { useState, useEffect } from "react";

export default function AboutUs() {

  return (
    <>
      <div className="flex flex-col md:flex-col w-full bg-gradient-to-r from-blue-200 via-blue-400 to-blue-200 text-black">

        <div className="flex flex-col md:flex-row w-full h-[200px]">
          <div className="w-full items-center justify-center h-full flex-row p-5 text-justify" >
            <div className="flex-col text-2xl font-bold uppercase tracking-wide">Welcome to Bestchill Inc.</div>
            <div className="flex-col text-lg font-bold uppercase tracking-wide">Your Trusted Partner in Advanced Cooling Solutions</div>
            <h2>
              



At Bestchill Inc., we specialize in high-performance, portable cooling systems designed to keep you—and your cargo—cool, safe, and comfortable. Whether you're on the road, at a campsite, or transporting perishable goods, we’ve got you covered.
            </h2>
          </div>
        </div>
      </div>
    </>
  );
}
