// tailwind.config.js

import React, { useState, useEffect } from "react";
const images = [
  "pic1.jpg",
  "pic2.jpg",
  "pic3.jpg",
  "pic4.jpg",
];

export default function Fst() {
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <div className="mx-auto max-w-lg overflow-hidden rounded-xl bg-white shadow-md md:max-w-4xl">
        <div className="flex ">
          <div className="p-8 flex-none w-64">
            <div className="text-sm font-semibold tracking-wide text-indigo-500 uppercase">Bestchill inc.,</div>
            <p className="mt-2 text-gray-500">
              Your Best Choice to Coolness!
            </p>
          </div>
          <div className="md:shrink-0 clip-custom-shape flex-none ">
            {/* <img
            class="h-48 w-full object-cover md:h-full md:w-48"
            src="/img/building.jpg"
            alt="Modern building architecture"
          /> */}
            <div className="animate-loopRightDouble">
              <img
                src={images[currentIndex]}
                alt={`Slide ${currentIndex + 1}`}
                className="h-48 w-full object-cover md:h-full md:w-48"
              />
              {/* Repeat if needed for smooth loop */}
              <img
                src={images[currentIndex]}
                alt={`Slide ${currentIndex + 4}`}
                className="h-48 w-full object-cover md:h-full md:w-48"
              />
            </div>
          </div>
        </div>


        
      </div>


    </>
  );
}
