import React, { useState } from 'react';

const Navbar1 = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav className="bg-blue-500 text-white px-4 py-3 flex items-center justify-between">
      {/* Logo */}
      <div >
      <div className="ml-2 z-1">
        <img src="logo.png" className="h-14" alt="Company Logo" />
      </div>
      <div className="text-2xl font-bold z-0">BESTCHILL INC.</div>
      </div>

      {/* Desktop Nav Links */}
      <ul className="hidden md:flex space-x-6">
        <li><a href="/" className="hover:text-gray-300">Home</a></li>
        <li><a href="/about" className="hover:text-gray-300">About</a></li>
        <li><a href="/services" className="hover:text-gray-300">Services</a></li>
        <li><a href="/contact" className="hover:text-gray-300">Contact</a></li>
      </ul>

      {/* Hamburger Icon (Mobile Only) */}
      <div
        className="md:hidden text-3xl cursor-pointer"
        onClick={toggleMobileMenu}
      >
        â˜°
      </div>

      {/* Mobile Menu Dropdown */}
      {isMobileMenuOpen && (
        <ul className="absolute top-16 right-4 bg-gray-700 text-white flex flex-col space-y-3 px-6 py-4 rounded-md shadow-md md:hidden z-50">
          <li><a href="/" className="hover:text-gray-300">Home</a></li>
          <li><a href="/about" className="hover:text-gray-300">About</a></li>
          <li><a href="/services" className="hover:text-gray-300">Services</a></li>
          <li><a href="/contact" className="hover:text-gray-300">Contact</a></li>
        </ul>
      )}
    </nav>
  );
};

export default Navbar1;
