// tailwind.config.js
module.exports = {
  theme: {
    extend: {
      keyframes: {
        loopRightDouble: {
          '0%': { transform: 'translateX(0)' },
          '100%': { transform: 'translateX(-50%)' }, // adjust for content width
        },
      },
      animation: {
        loopRightDouble: 'loopRightDouble 70s linear infinite',
      },
    },
  },
};


export default function Fst() {
  return (
    <div className="relative overflow-hidden w-full h-[500px]">
      <div className="flex animate-loopRightDouble w-max">
        <img src="pic1.jpg" className="h-full w-auto" alt=""/>
        <img src="pic2.jpg" className="h-full w-auto" alt=""/>
        {/* Repeat if needed for smooth loop */}
        <img src="pic1.jpg" className="h-full w-auto" alt=""/>
        <img src="pic2.jpg" className="h-full w-auto" alt=""/>
      </div>
    </div>
  );
}
