// tailwind.config.js

import React, { useState, useEffect } from "react";
const images = [
  "/attachment/pic1.jpg"
];

export default function Intro() {
  const [currentIndex, setCurrentIndex] = useState(0);
  // useEffect(() => {
  //   const interval = setInterval(() => {
  //     setCurrentIndex((prev) => (prev + 1) % images.length);
  //   }, 3000);

  //   return () => clearInterval(interval);
  // }, []);

  return (
    <>
    <div className="pt-[72px] h-full flex">

    
      <div className="w-full bg-yellow-500 hidden lg:flex md:flex-row">
        <div className="text-black h-full w-full align-center justify-center p-1  flex font-bold text-sm text-center">
          Your one-stop shop for smart, portable cooling. Whether you’re driving cross-country or camping under the stars, we help you stay cool and comfortable the whole way.
        </div>
      </div>
      <div className="flex items-center h-[500px] rounded-md w-full overflow-hidden
      bg-gradient-to-r from-blue-200 via-blue-400 to-blue-200">
        <div className="flex flex-col md:flex-row w-full h-full ">
          {/* <div className="p-8 w-64">
            <div className="text-2xl font-semibold tracking-wide text-indigo-500 uppercase">Bestchill inc.,</div>
            <p className="mt-2 text-gray-500">
              Your Best Choice to Coolness!
            </p>
          </div> */}
          <div className="p-8 md:w-1/2 w-full flex flex-col items-center justify-center h-full text-blue-900">
            <div className="text-4xl font-bold uppercase tracking-wide flex">
              {/* Your Best Choice to Coolness! */}
              𝕐𝕠𝕦𝕣
            </div>
            <div className="text-7xl font-bold uppercase tracking-wide flex">
              {/* Your Best Choice to Coolness! */}
              BEST
            </div>
            <div className="text-4xl font-bold uppercase tracking-wide flex">
              {/* Your Best Choice to Coolness! */}
              𝕔𝕙𝕠𝕚𝕔𝕖 𝕥𝕠
            </div>
            <div className="text-7xl font-bold uppercase tracking-wide flex">
              {/* Your Best Choice to Coolness! */}
              coolness!
            </div>
          </div>


          <div className="md:w-100% w-full">
            {/* <img
            class="h-48 w-full object-cover md:h-full md:w-48"
            src="/img/building.jpg"
            alt="Modern building architecture"
          />
           */}
            <div className="clip-custom-shape flex bg-yellow-500 items-center justify-center -ml-[5px] h-full">
              <div className="clip-custom-shape flex flex-col items-center justify-center ml-[10px] h-full right-1">
                <div className="animate-loopRightDouble flex">
                  <img
                    src={images[currentIndex]}
                    alt={`Slide ${currentIndex + 1}`}
                    className="flex h-full w-full object-cover md:h-full md:w-full"
                  />
                  {/* Repeat if needed for smooth loop */}
                  <img
                    src={images[currentIndex]}
                    alt={`Slide ${currentIndex + 1}`}
                    className="flex h-full w-full object-cover md:h-full md:w-full"
                  />

                  <img
                    src={images[currentIndex]}
                    alt={`Slide ${currentIndex + 1}`}
                    className="flex h-full w-full object-cover md:h-full md:w-full"
                  />

                  <img
                    src={images[currentIndex]}
                    alt={`Slide ${currentIndex + 1}`}
                    className="flex h-full w-full object-cover md:h-full md:w-full"
                  />
                  <img
                    src={images[currentIndex]}
                    alt={`Slide ${currentIndex + 1}`}
                    className="flex h-full w-full object-cover md:h-full md:w-full"
                  />
                  <img
                    src={images[currentIndex]}
                    alt={`Slide ${currentIndex + 1}`}
                    className="flex h-full w-full object-cover md:h-full md:w-full"
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
</div>


      </div>


    </>
  );
}
