import React, { useState, useEffect } from "react";

import Prodreefer from "../components/Prodreefer.jsx";

const images = [
  "/attachment/pic1.jpg",
  "/attachment/pic2.jpg",
  "/attachment/pic3.jpg",
  "/attachment/pic4.jpg",
];

export default function Products() {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto loop every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  // Manual navigation
  const goPrev = () => {
    setCurrentIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const goNext = () => {
    setCurrentIndex((prev) => (prev + 1) % images.length);
  };

  return (
    <>

      <div className="flex mx-auto w-full items-center justify-center p-4">
        <h1 className="text-4xl font-bold text-indigo-600 uppercase tracking-wide">
          Product
        </h1>
      </div>

      <div className="flex flex-col md:flex-row items-center bg-white rounded-md w-full overflow-hidden m">
        <ProductsReefer></ProductsReefer>
      </div>
      <div>
        <button>
          See more Product!
        </button>
      </div>
    </>
  );
}
