import React, { useState, useEffect } from "react";

import Prodreefer from "../components/Prodreefer.jsx";
import ProductsReefer from "../components/ProductsReefer.jsx";
import ProductsPortable from "../components/ProductsPortable.jsx";

const pages = [
  <ProductsReefer />,
  <ProductsPortable />,
];

export default function Products3() {
  const [currentPage, setCurrentPage] = useState(0);

  const products = [
    ['reefer system', '/attachment/Reefer.png',
    ['/attachment/reeferbox.png', 'Reefer Box', 'Details of Reefer Box'],
    ['/attachment/reeferaircon.png', 'Air condition', 'Details of Air condition'],
    ['/attachment/curtains.png', 'curtain', 'Details of curtain']
    ],
    ['portable aircon', '/attachment/Reefer.png',
    ['/attachment/reeferbox.png', 'Reefer Box', 'Details of Reefer Box'],
    ['/attachment/reeferaircon.png', 'Air condition', 'Details of Air condition'],
    ['/attachment/curtains.png', 'curtain', 'Details of curtain']
    ],
    ['camping set', '/attachment/Reefer.png',
    ['/attachment/reeferbox.png', 'Reefer Box', 'Details of Reefer Box'],
    ['/attachment/reeferaircon.png', 'Air condition', 'Details of Air condition'],
    ['/attachment/curtains.png', 'curtain', 'Details of curtain']
    ]
  ]

  const rowCount = reefer.length;

  // // Auto loop every 3 seconds
  //   useEffect(() => {
  //     const interval = setInterval(() => {
  //       setCurrentPage((prev) => (prev + 1) % pages.length);
  //     }, 10000);

  //     return () => clearInterval(interval);
  //   }, []);

  return (
    <>
      <div className="
      flex flex-col
      items-center 
      h-full w-full
      rounded-md 
      overflow-hidden
      bg-gradient-to-r from-blue-200 via-blue-400 to-blue-200
      ">
        <div className="w-full flex bg-yellow-500 h-1" />

        <div className="w-full h-auto bg-black top-0">
          <h1 className="text-4xl font-bold text-white uppercase tracking-wide justify-center flex">
            our Products
          </h1>
        </div>

        {products.map((products, productindex) => (
          <>
            
            


            <div key={productindex} className="w-full bg-white top-0 flex flex-col h-full w-full text-black">

              <div className="w-full h-[10%] flex items-center justify-center">
                <h1 className="text-3xl font-bold uppercase tracking-wide">
                  {products[0]}
                </h1>
              </div>

              <div className="w-full flex flex-col lg:flex-row  outline outline-1 outline-black-500">
                <div className="flex outline w-full">
                  <div className="flex mx-auto items-center">
                    <img className="lg:scale-75 flex"
                      src={products[1]} alt="" />
                  </div>
                </div>
                <div className="flex outline w-full p-5 flex-col gap-3">
                  {productindex.flat(2).map((cell, index) => (
                    <>
                      <div key={index} className="flex flex-row gap-4" style={{ height: `${100 / rowCount}%` }}>
                        <div className="w-[30%] flex outline">
                          <img
                            src={cell[0]}
                            alt=""
                            className="object-fill"
                          />
                        </div>
                        <div className="outline w-full flex flex-col">
                          <h2 className="text-lg font-bold uppercase tracking-wide">
                            {'>'} {cell[1]}
                          </h2>
                          <h2 className="text-md uppercase tracking-wide">
                            {cell[2]}
                          </h2>
                        </div>
                      </div>
                    </>
                  ))}
                </div>
              </div>



            </div>
          </>
        ))}

      </div >


    </>
  );
}
