// tailwind.config.js

import React, { useState, useEffect } from "react";
const images = [
  "/attachment/download.jpeg"
];

export default function Intro() {
  const [currentIndex, setCurrentIndex] = useState(0);
  // useEffect(() => {
  //   const interval = setInterval(() => {
  //     setCurrentIndex((prev) => (prev + 1) % images.length);
  //   }, 3000);

  //   return () => clearInterval(interval);
  // }, []);

  return (
    <>
      <div className="flex w-full md:w-full grid grid-cols-5 bg-white flex-col">
      {/* // flex flex-col md:flex-row items-center bg-white rounded-md w-full overflow-hidden"> */}
        
          <div className="p-8 w-full flex items-center justify-center col-span-2">
            <h1 className="text-3xl font-bold text-indigo-600 uppercase tracking-wide">
              Your Best Choice to Coolness!
            </h1>
          </div>

          <div className="w-full col-span-3 col-start-3">
            <div className="clip-custom-shape flex flex-col items-center justify-center -ml-[70px] right-1">
              <div className="animate-loopRightDouble flex">
                <img
                  src={images[currentIndex]}
                  alt={`Slide ${currentIndex + 1}`}
                  className="h-48 w-full object-cover md:h-48 md:w-full"
                />
                {/* Repeat if needed for smooth loop */}
                
              </div>
            </div>
          </div>
      </div>


    </>
  );
}
